import React, { useState, useEffect } from 'react';
import './PortfolioSelection.css';
import ProjectPage from './ProjectPage';

const PortfolioSelection = () => {
  const [hoveredProject, setHoveredProject] = useState(null);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [selectedProject, setSelectedProject] = useState(null);

  // Add class to body to override main styles
  useEffect(() => {
    document.body.classList.add('portfolio-active');

    return () => {
      document.body.classList.remove('portfolio-active');
    };
  }, []);

  const handleMouseMove = (e, projectId) => {
    const rect = e.currentTarget.getBoundingClientRect();
    setMousePosition({
      x: e.clientX - rect.left,
      y: e.clientY - rect.top
    });
  };

  const handleMouseEnter = (projectId) => {
    setHoveredProject(projectId);
  };

  const handleMouseLeave = () => {
    setHoveredProject(null);
  };

  const handleProjectClick = (project) => {
    setSelectedProject(project);
  };

  const handleBackToList = () => {
    setSelectedProject(null);
  };

  const handleNextProject = () => {
    const currentIndex = projects.findIndex(p => p.id === selectedProject.id);
    const nextIndex = (currentIndex + 1) % projects.length;
    setSelectedProject(projects[nextIndex]);
  };

  const handlePreviousProject = () => {
    const currentIndex = projects.findIndex(p => p.id === selectedProject.id);
    const prevIndex = currentIndex === 0 ? projects.length - 1 : currentIndex - 1;
    setSelectedProject(projects[prevIndex]);
  };

  const projects = [
    {
      id: 1,
      title: "Limnia",
      subtitle: "Brand Identity & Web Design",
      image: "https://images.unsplash.com/photo-1460925895917-afdab827c52f?w=800&h=600&fit=crop",
      category: "Branding",
      year: "2024",
      description: "Built on the idea that life is yours for the making, LIMNIA is a modular fine jewelry brand that, in its modularity, empowers you to live exactly as you are in a world of limitless possibilities.",
      detailedDescription: "The key feature of the website is its modular jewelry builder created to address customers' evolving needs. With LIMNIA, we've built a clever, customizable jewelry line that morphs with you. A necklace becomes a pair of anklets; an earring turns into a ring.",
      technologies: ["React", "Node.js", "MongoDB", "GSAP"],
      client: "Limnia Jewelry",
      timeline: "4 months",
      link: "https://limnia.com"
    },
    {
      id: 2,
      title: "Dennis Berti",
      subtitle: "Portfolio Website",
      image: "https://images.unsplash.com/photo-1517077304055-6e89abbf09b0?w=800&h=600&fit=crop",
      category: "Web Design",
      year: "2024",
      description: "Modern portfolio website with interactive elements and smooth animations for a digital artist.",
      detailedDescription: "This project focused on creating a unique digital presence that showcases artistic work while maintaining excellent user experience and performance.",
      technologies: ["Vue.js", "GSAP", "CSS3", "WebGL"],
      client: "Dennis Berti Studio",
      timeline: "2 months"
    },
    {
      id: 3,
      title: "Epicurrence",
      subtitle: "Event Branding",
      image: "https://images.unsplash.com/photo-1558655146-9f40138edfeb?w=800&h=600&fit=crop",
      category: "Branding",
      year: "2023",
      description: "Complete event branding package including logo, merchandise, and digital assets for a design conference.",
      detailedDescription: "A comprehensive branding solution that brings together physical and digital experiences for one of the most anticipated design conferences of the year.",
      technologies: ["Figma", "Illustrator", "Photoshop", "After Effects"],
      client: "Design Events Inc",
      timeline: "3 months"
    },
    {
      id: 4,
      title: "Max Shkret",
      subtitle: "Creative Direction",
      image: "https://images.unsplash.com/photo-1586717791821-3f44a563fa4c?w=800&h=600&fit=crop",
      category: "Creative",
      year: "2023",
      description: "Creative direction and visual identity for an innovative photographer and visual artist.",
      detailedDescription: "This project challenged us to create a visual language that represents artistic vision while maintaining commercial appeal.",
      technologies: ["Photoshop", "Lightroom", "Figma"],
      client: "Max Shkret",
      timeline: "2 months"
    },
    {
      id: 5,
      title: "Adobe Editorial Kit",
      subtitle: "UI/UX Design",
      image: "https://images.unsplash.com/photo-1559028006-448665bd7c7f?w=800&h=600&fit=crop",
      category: "UI/UX",
      year: "2024",
      description: "Comprehensive design system and editorial toolkit for creative professionals.",
      technologies: ["Figma", "Sketch", "Adobe XD"],
      client: "Adobe",
      timeline: "6 months"
    },
    {
      id: 6,
      title: "Cure",
      subtitle: "Healthcare App",
      image: "https://images.unsplash.com/photo-1576091160399-112ba8d25d1f?w=800&h=600&fit=crop",
      category: "App Design",
      year: "2023",
      description: "Revolutionary healthcare application focused on personalized medicine and patient care.",
      technologies: ["React Native", "Node.js", "PostgreSQL"],
      client: "Cure Health",
      timeline: "8 months"
    },
    {
      id: 7,
      title: "Aeditor",
      subtitle: "Mobile Application",
      image: "https://images.unsplash.com/photo-1512941937669-90a1b58e7e9c?w=800&h=600&fit=crop",
      category: "App Design",
      year: "2024",
      description: "Advanced mobile editing application with AI-powered features.",
      technologies: ["Swift", "Kotlin", "TensorFlow"],
      client: "Aeditor Inc",
      timeline: "5 months"
    },
    {
      id: 8,
      title: "Rafal Bojar",
      subtitle: "Personal Branding",
      image: "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=800&h=600&fit=crop",
      category: "Branding",
      year: "2023",
      description: "Complete personal branding and digital presence for a renowned photographer.",
      technologies: ["WordPress", "PHP", "JavaScript"],
      client: "Rafal Bojar",
      timeline: "3 months"
    },
    {
      id: 9,
      title: "IL Makiage",
      subtitle: "E-commerce Platform",
      image: "https://images.unsplash.com/photo-1556742049-0cfed4f6a45d?w=800&h=600&fit=crop",
      category: "E-commerce",
      year: "2024",
      description: "Revolutionary beauty e-commerce platform with AR try-on features.",
      technologies: ["React", "Redux", "AR.js"],
      client: "IL Makiage",
      timeline: "7 months"
    },
    {
      id: 10,
      title: "Honey Pot",
      subtitle: "Brand Strategy",
      image: "https://images.unsplash.com/photo-1560472354-b33ff0c44a43?w=800&h=600&fit=crop",
      category: "Branding",
      year: "2023",
      description: "Natural beauty brand strategy and visual identity development.",
      technologies: ["Illustrator", "Photoshop", "InDesign"],
      client: "Honey Pot Co",
      timeline: "4 months"
    },
    {
      id: 11,
      title: "Adobe XD Kit",
      subtitle: "Design System",
      image: "https://images.unsplash.com/photo-1581291518857-4e27b48ff24e?w=800&h=600&fit=crop",
      category: "UI/UX",
      year: "2024",
      description: "Comprehensive design system and component library for modern applications.",
      technologies: ["Adobe XD", "Figma", "React"],
      client: "Adobe",
      timeline: "5 months"
    },
    {
      id: 12,
      title: "Uruoi",
      subtitle: "Beauty Brand",
      image: "https://images.unsplash.com/photo-1596462502278-27bfdc403348?w=800&h=600&fit=crop",
      category: "Branding",
      year: "2023",
      description: "Japanese-inspired beauty brand with focus on minimalism and natural ingredients.",
      technologies: ["Figma", "Photoshop", "Web Design"],
      client: "Uruoi Beauty",
      timeline: "6 months"
    }
  ];

  const formatNumber = (num) => {
    return num.toString().padStart(2, '0');
  };

  return (
    <div className="portfolio-container">
      <div className="portfolio-header">
        <div className="header-right">
          <p>Selected Work</p>
        </div>
      </div>

      <div className="projects-list">
        {projects.map((project, index) => (
          <div 
            key={project.id}
            className={`project-item ${hoveredProject === project.id ? 'hovered' : ''}`}
            onMouseEnter={() => handleMouseEnter(project.id)}
            onMouseLeave={handleMouseLeave}
            onMouseMove={(e) => handleMouseMove(e, project.id)}
          >
            <div className="project-number">
              {formatNumber(project.id)}
            </div>
            <div className="project-content">
              <div className="project-info">
                <h3 className="project-title">{project.title}</h3>
                <p className="project-subtitle">{project.subtitle}</p>
                <div className="project-meta">
                  <span className="project-category">{project.category}</span>
                  <span className="project-year">{project.year}</span>
                </div>
              </div>
            </div>

            {/* Cursor Following Image */}
            <div 
              className="cursor-image"
              style={{
                transform: `translate(${mousePosition.x}px, ${mousePosition.y}px)`,
                opacity: hoveredProject === project.id ? 1 : 0
              }}
            >
              <img 
                src={project.image} 
                alt={project.title}
                loading="lazy"
              />
              <div className="image-overlay">
                <span>View Project</span>
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="portfolio-footer">
        <div className="scroll-indicator">
          <p>SCROLL DOWN FOR ALL CASES</p>
        </div>
        <div className="social-links">
          <a href="#dribbble" className="social-link">Dribbble</a>
          <a href="#behance" className="social-link">Behance</a>
          <a href="#twitter" className="social-link">Twitter</a>
        </div>
      </div>
    </div>
  );
};

export default PortfolioSelection;
